    static class Interval {
        final int from;
        final int to;

        Interval(int from, int to) {
            this.from = from;
            this.to = to;
        }

        int length() {
            return to - from + 1;
        }

        public boolean hasIntersectionOrNeighbour(Interval other) {
            Interval left;
            Interval right;

            if (from < other.from) {
                left = this;
                right = other;
            } else {
                left = other;
                right = this;
            }

            if (left.to >= right.from) {
                return true;
            }

            return left.to + 1 == right.from;
        }

        public Interval merge(Interval other) {
            return new Interval(Math.min(from, other.from), Math.max(to, other.to));
        }

        @Override
        public String toString() {
            return "[%d; %d]".formatted(from, to);
        }
    }

    /** time: O(N) space: O(N) */
    public static int longestValidParentheses(String str) {

        Deque<Interval> intervals = new ArrayDeque<>();

        Deque<Integer> stack = new ArrayDeque<>();

        int maxSubLength = 0;

        for (int i = 0; i < str.length(); ++i) {
            char ch = str.charAt(i);

            if (ch == '(') {
                stack.push(i);
            } else {
                if (!stack.isEmpty()) {
                    int startOfInterval = stack.pop();

                    Interval cur = new Interval(startOfInterval, i);

                    if (intervals.isEmpty()) {
                        maxSubLength = Math.max(maxSubLength, cur.length());
                        intervals.push(cur);
                    } else {
                        while (!intervals.isEmpty()) {
                            Interval prev = intervals.pop();

                            if (prev.hasIntersectionOrNeighbour(cur)) {
                                cur = prev.merge(cur);
                            } else {
                                intervals.push(prev);
                                break;
                            }
                        }

                        maxSubLength = Math.max(maxSubLength, cur.length());
                        intervals.push(cur);
                    }
                }
            }
        }

        return maxSubLength;
    }

    /** Time limit exceeded. time: O(N^3) space: O(N^2) */
    public static int longestValidParenthesesDynamic(String str) {

        final char[] arr = str.toCharArray();
        final int length = arr.length;
        boolean[][] sol = new boolean[length][length];

        int maxValidSubstrLength = 0;

        for (int row = length - 2; row >= 0; --row) {
            for (int col = row + 1; col < length; ++col) {

                if (arr[row] == '(' && arr[col] == ')') {

                    boolean isCurSubValid = false;

                    if (solution(sol, row + 1, col - 1)) {
                        isCurSubValid = true;
                    } else {
                        for (int k = row + 1; k < col - 1; ++k) {
                            if (arr[k] == ')'
                                    && solution(sol, row + 1, k - 1)
                                    && solution(sol, k + 1, col)) {
                                isCurSubValid = true;
                                break;
                            }
                        }
                    }

                    if (isCurSubValid) {
                        sol[row][col] = true;
                        maxValidSubstrLength = Math.max(maxValidSubstrLength, col - row + 1);
                    }
                }
            }
        }

        return maxValidSubstrLength;
    }

    private static boolean solution(boolean[][] sol, int row, int col) {
        if (row > col) {
            return true;
        }
        return sol[row][col];
    }
